INSERT INTO `brand` (`id`, `ufu`, `name`, `dsc`, `externalId`, `logoFn`, `nameCanonical`) VALUES
(1, 'came', 'Came', 'CAME - производитель широкого ассортимента автоматики для въездных, гаражных и промышленных ворот, дорожных шлагбаумов, автомобильных парковок и систем контроля доступа, а также разработчик систем \"умный дом\" и охранных сигнализаций.', 2, 'ab92e3ad-d327-4636-abad-eb48020d8d69', 'came'),
(2, 'effeff', 'Effeff', 'ASSA ABLOY Sicherheitstechnik GmbHКомпания занимается разработкой, производством и распространением высококачественной продукции и многофункциональных систем для частного, коммерческого и общественного пользования. Наши известные бренды: ASSA, KESO, IKON и effeff.', 21, '124679cd-1381-4913-9b72-c0d1a629dec8', 'effeff'),
(3, 'genius', 'Genius', 'Компания Genius была основана в Италии и более 20 лет занималась производством автоматизированных систем доступа: приводы для различного типа ворот, автоматические шлагбаумы и дополнительное оборудование.В настоящее время бренд Genius входит в известный во всём мире концерн FAAC Group, который является лидером в области автоматизированных систем доступа.', 26, '1dffa61f-f362-43c1-91a4-3d6f50370919', 'genius'),
(4, 'hikvision', 'Hikvision', 'Hikvision - ведущий мировой поставщик инновационных продуктов и систем безопасности.Комплексные продуктовые решения Hikvision включают в себя: системы видеонаблюдения, видеостены, СКУД, домофонию, охранные сигнализации. Hikvision продвигает ключевые технологии кодирования аудио и видео, обработки видеоизображения и хранения данных, а также такие перспективные технологии, как облачные вычисления, big data и deep learning (глубокое обучение). Помимо рынка видеонаблюдения Hikvision предлагает решения для умного дома, промышленной автоматизации и робототехники согласно своей долгосрочной стратегии развития.', 28, 'f9e44ece-2d2e-493d-bfa7-c4b40cf3b582', 'hikvision'),
(5, 'ironlogic', 'IronLogic', 'IronLogic - производитель оборудования для систем контроля доступа.', 32, '05b4db32-fa99-4da9-be64-da125f21013b', 'ironlogic'),
(6, 'rusguard', 'RusGuard', 'Мы делаем эффективную и современную систему контроля и управления доступом RusGuard: производим сетевые контроллеры и считыватели, а также разрабатываем программное обеспечение.', 54, '965b0743-c4a4-45c2-8ddf-20aa625fad36', 'rusguard'),
(7, 'trassir', 'Trassir', 'Trassir - российский производитель систем видеонаблюдения.Среди нашей продукции: широкая линейка видеорегистраторов, IP-камеры различного радиуса действия и углов обзора: с ИК-подсветкой и сверхширокоугольными объективами, а также программные модули для бизнес-аналитики, видео-, аудио- и нейроаналитики.', 59, '687900ef-8f12-47ce-a53e-c67f1b9ddbb2', 'trassir'),
(8, 'doorhan', 'DoorHan', 'Автоматические гаражные ворота, роллетные системы, автоматика для ворот, дверные системы, шлагбаумы производство.Международный концерн DoorHan успешно работает на рынке подвижных ограждающих конструкций с 1994 года и является крупнейшим в России производителем автоматических ворот, рольставен, дверей, алюминиевых систем, модульных конструкций и перегрузочного оборудования. Кроме того, концерн DoorHan - единственный в России производитель комплектующих для всех типов автоматических ворот и роллет.', 183, '209c2bf0-4fb5-429e-8faa-e754a50e18bb', 'doorhan'),
(9, 'abloy', 'Abloy', 'Финский производитель замков, систем запирания и строительных скобяных изделий, а также разработчик изделий в области технологии электромеханических замков.', 203, 'e8b7b5be-8b23-4b3b-a054-fa0f70d3445c', 'abloy'),
(10, 'antrex', 'Antrex', '-', 311, 'd4b6bfd6-bfe0-42f8-8f38-150dfce36099', 'antrex'),
(11, 'tehnologiya', 'Технология', 'Технология - крупнейший изготовитель дорожного и парковочного оборудования. Наша продукция: лежачие полицейские (идн) демпферы стеновые демпферы угловые резиновые колесоотбойники делиниаторы кабель-каналы съезды с бордюра знаки дорожные зеркала сферические парковочные столбики конусы сигнальные гибкие столбики водоналивные блоки вехи оградительные ограждение \"Солдатик\" сетки оградительные фонари сигнальные сигнальные ленты металлические колесоотбойники велопарковки блокираторы парковочного места сигнальные столбики шлагбаумы и многое другое)', 332, '2e5c0556-87ba-4d7a-aedc-acf5ca4cddc9', 'tehnologiya'),
(12, 'accordtec', 'AccordTec', 'AccordTec - российский бренд в области систем безопасности, принадлежит Группе компаний Аккорд-СБ, работающей на российском рынке систем безопасности и контроля доступа с 1995 года. Началом деятельности компании был выпуск первого в России электромагнитного замка собственного производства ML-194, зарекомендовавшего себя как образец надежности и качества. Сегодня бренд Accordtec - лидер в области производства электромагнитных замков различных модификаций.', 339, '0ea10b60-b4cb-42bc-a48f-cba6cc54e408', 'accordtec'),
(13, 'tantos', 'Tantos', 'Производитель \"Tantos\" создан командой опытных специалистов, которые прекрасно понимают каким требованиям должно соответствовать современное оборудование. Знания и умения разработчиков воплотились в самые передовые решения.Чтобы стать \"Tantos\", оборудованию необходимо отвечать следующим требованиям: - Находиться на пике технического прогресса:Мы убеждены, что наши клиенты достойны только инновационных технологий - Быть оптимальным по соотношению цена/качество:За свои деньги наши клиенты должны получать максимум - Быть удобным в установке, настройке и эксплуатации:Для нас не существует мелочей. Все операции по установке и настройке максимально упрощены для монтажника, а интерфейс нашего оборудования самый дружелюбный для пользователя. - Иметь не менее 30 000 часов наработки на отказ в самых суровых условиях:Наше оборудование предназначено для России, и оно работает как в условиях полярного круга, так и в субтропиках - Иметь изящный и современный дизайн:Мы никогда не забываем, что пользователям наших систем предстоит каждый день видеть их. Поэтому наше оборудование гармонично впишется в окружающий интерьер.', 464, '1c0db474-7585-421e-b321-eb6a98dc0759', 'tantos'),
(14, 'digital-duplex', 'Digital Duplex', 'Переговорные устройства клиент-кассир Digital DuplexПереговорное устройство клиент-кассир просто и удобно и во время эксплуатации. Оператор понижает и повышает громкость звука, включает и отключает питание. Также он может отключить микрофон, чтобы ни один клиент не услышал, что происходит в помещении. Переговорные устройства клиент кассир приобретают в первую очередь банковские учреждения, где имеются отделы по работе с клиентами. А также автозаправочные станции, медицинские кабинеты, вокзалы и другие организации, где разговоры с клиентом ведутся через перегородку.', 494, 'bb885a61-1e39-4937-9b41-6ca460741a07', 'digitalduplex'),
(15, 'security-force', 'Security Force', '-', 500, '5a2250d8-a2c2-4d6e-bdbc-cd764b463d90', 'securityforce'),
(16, 'hiwatch', 'HiWatch', 'Hikvision - ведущий мировой поставщик инновационных продуктов и систем безопасности.Комплексные продуктовые решения Hikvision включают в себя: системы видеонаблюдения, видеостены, СКУД, домофонию, охранные сигнализации. Hikvision продвигает ключевые технологии кодирования аудио и видео, обработки видеоизображения и хранения данных, а также такие перспективные технологии, как облачные вычисления, big data и deep learning (глубокое обучение). Помимо рынка видеонаблюдения Hikvision предлагает решения для умного дома, промышленной автоматизации и робототехники согласно своей долгосрочной стратегии развития.', 527, '54aec095-ebfc-4add-a50e-81029ea2b120', 'hiwatch'),
